import React, { useEffect, useState } from 'react';

function HomeScreen() {
  const [text, setText] = useState('');
  const [isHovered, setIsHovered] = useState(false);
  const fullText = "Developer, Designer, Researcher";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index + 1));
      index++;
      if (index === fullText.length) {
        clearInterval(interval);
      }
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={styles.homeContainer}>
      <div style={styles.content}>
        <h1 style={styles.title}>Hello.</h1>
        <h1 style={styles.title}>Iâ€™m Wesley Duanrui Lu.</h1>
        <h1 style={styles.typing}>{text}</h1>
        <button 
          style={isHovered ? {...styles.resumeButton, ...styles.resumeButtonHover} : styles.resumeButton}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
          onClick={() => window.open('https://drive.google.com/file/d/1ENMeEVrrUhLOj9VoaPdo29svIi_kAFus/view?usp=sharing', '_blank')}
        >
          View Resume
        </button>
      </div>
    </div>
  );
}

const styles = {
  homeContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    padding: '0px',
    height: '100vh',
    background: 'linear-gradient(135deg, rgba(255, 61, 0, 0.6), rgba(255, 138, 0, 0.4), rgba(255, 236, 0, 0.2), #ffffff)',
    backgroundSize: '400% 400%',
    animation: 'lightFadeAnimation 15s ease infinite',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100vh',
    paddingLeft: '100px',
    textAlign: 'left',
  },
  title: {
    fontSize: '80px',
    fontWeight: 'bold',
    color: '#000000',
    margin: '0px 0 10px 0',
  },
  typing: {
    fontSize: '80px',
    fontWeight: 'bold',
    background: 'linear-gradient(90deg, #A4508B, #5F0A87, #007AFF)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    borderRight: '2px solid #000',
    margin: '5px 0',
  },
  resumeButton: {
    marginTop: '20px',
    padding: '12px 24px',
    fontSize: '18px',
    fontWeight: 500,
    color: '#007aff',
    backgroundColor: 'transparent',
    border: '2px solid #007aff',
    borderRadius: '12px',
    cursor: 'pointer',
    outline: 'none',
    width: '200px',
    textAlign: 'center',
    transition: 'all 0.3s ease',
  },
  resumeButtonHover: {
    color: '#fff',
    background: 'linear-gradient(90deg, #A4508B, #5F0A87, #007AFF)',
    transform: 'scale(1.1)',
  },
};

// Keyframe animation for the light fade effect
const lightFadeAnimation = `
  @keyframes lightFadeAnimation {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }
`;

// Add the keyframes to the document
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(lightFadeAnimation, styleSheet.cssRules.length);

export default HomeScreen;
