import React, { useEffect, useRef } from 'react';

function CanvasImage() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = 'img/apptroniks.png'; // Specify your image path here

    img.onload = () => {
      // Resize the canvas to fit the image or set specific dimensions
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
    };
  }, []);

  return <canvas ref={canvasRef} style={styles.canvas} />;
}

function AboutMe() {
  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.title}>About Me.</h1>
        <p style={styles.paragraph}>
          I'm Wesley Lu, a computer science student at Georgia Tech specializing in Artificial Intelligence and System Architecture. I'm passionate about finance and technology, with a strong focus on developing algorithmic trading strategies powered by machine learning.
        </p>
        <p style={styles.paragraph}>
          Outside of coding, I appreciate nature through exercising, sightseeing, and snowboarding. I enjoy staying active, trying new restaurants, and exploring new hobbies/interests. I also enjoy poker and chess.
        </p>
      </div>
      <div style={styles.canvasContainer}>
        <CanvasImage />
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    maxWidth: '60vw',
    padding: '2vw',
    margin: '0 auto',
    color: '#333',
    fontFamily: 'Roboto, Arial, sans-serif',
    backgroundColor: '#ffffff',
    minHeight: '50vh',
  },
  textContainer: {
    flex: 1,
    marginRight: '2vw',
    textAlign: 'left',
  },
  canvasContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    width: '30vw', // Adjusted width to accommodate the canvas size
  },
  canvas: {
    width: '100%', // Adjusts canvas width relative to its container
    height: 'auto', // Keeps aspect ratio of the image
    borderRadius: '1vw',
  },
  title: {
    fontSize: '3vw',
    fontWeight: 'bold',
    color: '#000000',
    marginBottom: '1vh',
  },
  paragraph: {
    fontSize: '1.2vw',
    lineHeight: '1.6',
    marginBottom: '1vh',
    fontWeight: '400',
  },
};

export default AboutMe;
