import React, { useEffect, useState } from 'react';

function HomeScreen() {
  const [text, setText] = useState('');
  const [showNavbar, setShowNavbar] = useState(true); // State to toggle navbar visibility
  const [lastScrollY, setLastScrollY] = useState(0); // Track last scroll position
  const fullText = "developer, designer, researcher";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setText(fullText.slice(0, index + 1));
      index++;
      if (index === fullText.length) {
        clearInterval(interval);
      }
    }, 100);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > lastScrollY) {
        setShowNavbar(false); // Hide navbar on scroll down
      } else {
        setShowNavbar(true); // Show navbar on scroll up
      }
      setLastScrollY(window.scrollY);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  return (
    <div style={styles.homeContainer}>
      {showNavbar && ( // Conditionally render the navbar based on scroll
        <nav style={styles.navbar}>
          <ul style={styles.navLinks}>
            <li>home</li>
            <li>about</li>
            <li>experience</li>
            <li>projects</li>
            <li>skills</li>
            <li>resume</li>
          </ul>
        </nav>
      )}
      <div style={styles.content}>
        <h1 style={styles.title}>hello.</h1>
        <h1 style={styles.title}>iâ€™m wesley lu.</h1>
        <h1 style={styles.typing}>{text}</h1>
      </div>
    </div>
  );
}

const styles = {
  homeContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    padding: '20px',
    height: '100vh',
    backgroundColor: '#ffffff',
  },
  navbar: {
    width: '100%',
    padding: '20px 40px',
    display: 'flex',
    justifyContent: 'flex-start',
    backgroundColor: '#ffffff',
    position: 'fixed',
    top: 0,
    left: 0,
    zIndex: 1,
    transition: 'top 0.3s', // Smooth transition for hide/show effect
  },
  navLinks: {
    listStyleType: 'none',
    display: 'flex',
    gap: '20px',
    padding: 0,
    margin: 0,
    fontSize: '18px',
    color: '#333',
    fontWeight: 'bold',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100vh',
    paddingLeft: '40px',
    textAlign: 'left',
  },
  title: {
    fontSize: '80px',
    fontWeight: 'bold',
    color: '#000000',
    margin: '0px 0 10px 0',
  },
  typing: {
    fontSize: '80px',
    fontWeight: 'bold',
    background: 'linear-gradient(90deg, #A4508B, #5F0A87, #007AFF)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    borderRight: '2px solid #000',
    animation: 'blink 0.75s step-end infinite',
    margin: '5px 0',
  },
};

export default HomeScreen;
