import React, { useEffect, useRef, useState } from 'react';

const images = [
  'img/apptroniks.png',
  'img/gates.png',
  'img/laketahoesnowboard.png',
  'img/sunset.png',
];

function CanvasImage() {
  const canvasRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const loadAndDrawImage = (src) => {
      const img = new Image();
      img.src = src;

      img.onload = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };
    };

    loadAndDrawImage(images[currentIndex]);

    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(interval);
  }, [currentIndex]);

  return <canvas ref={canvasRef} style={styles.canvas} />;
}

function AboutMe() {
  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.title}>About Me.</h1>
        <p style={styles.paragraph}>
          I'm Wesley Lu, a computer science student at Georgia Tech specializing in Artificial Intelligence and System Architecture. I'm passionate about finance and technology, with a strong focus on developing algorithmic trading strategies powered by machine learning.
        </p>
        <p style={styles.paragraph}>
          Outside of coding, I appreciate nature through exercising, sightseeing, and snowboarding. I enjoy staying active, trying new restaurants, and exploring new hobbies/interests. I also enjoy poker and chess.
        </p>
      </div>
      <div style={styles.canvasContainer}>
        <CanvasImage />
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    maxWidth: '60vw',
    padding: '10rem',
    margin: '0 auto',
    color: '#333',
    fontFamily: 'Roboto, Arial, sans-serif',
    backgroundColor: '#ffffff',
    minHeight: '50rem',
  },
  textContainer: {
    flex: 1,
    marginRight: '2rem',
    textAlign: 'left',
  },
  canvasContainer: {
    width: '20rem',
    height: '20rem',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  canvas: {
    width: '100%',
    height: 'auto',
    borderRadius: '1rem',
  },
  title: {
    fontSize: '4.5rem',
    fontWeight: 'bold',
    color: '#000000',
    marginBottom: '1rem',
  },
  paragraph: {
    fontSize: '2.5rem',
    lineHeight: '1.6',
    marginBottom: '1rem',
    fontWeight: '400',
  },
};

export default AboutMe
