import React, { useEffect, useState } from 'react';

function HomeScreen() {
  const [text, setText] = useState('');
  const fullText = "developer, designer. researcher.";

  useEffect(() => {
    let index = 0;
    const typeText = () => {
      setText('');
      const interval = setInterval(() => {
        setText(fullText.slice(0, index + 1));
        index++;
        if (index === fullText.length) {
          clearInterval(interval);
          setTimeout(typeText, 3000); // Restart the typing animation every 3 seconds after completion
        }
      }, 100); // Adjust typing speed here
    };
    typeText();
  }, []);

  return (
    <div style={styles.homeContainer}>
      <nav style={styles.navbar}>
        <ul style={styles.navLinks}>
          <li>home</li>
          <li>about</li>
          <li>experience</li>
          <li>projects</li>
          <li>skills</li>
          <li>resume</li>
        </ul>
      </nav>
      <div style={styles.content}>
        <h1 style={styles.title}>hello.</h1>
        <h1 style={styles.title}>iâ€™m wesley lu.</h1>
        <h1 style={styles.typing}>{text}</h1>
      </div>
    </div>
  );
}

const styles = {
  homeContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    padding: '20px',
    height: '100vh',
    backgroundColor: '#ffffff',
  },
  navbar: {
    width: '100%',
    padding: '20px 40px',
    display: 'flex',
    justifyContent: 'flex-start',
    backgroundColor: '#ffffff',
    position: 'fixed',
    top: 0,
    left: 0,
    zIndex: 1,
  },
  navLinks: {
    listStyleType: 'none',
    display: 'flex',
    gap: '20px',
    padding: 0,
    margin: 0,
    fontSize: '18px',
    color: '#333',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100vh',
    paddingLeft: '40px',
    textAlign: 'left',
  },
  title: {
    fontSize: '80px',
    fontWeight: '500',
    color: '#000000',
    margin: '5px 0',
    fontFamily: 'Roboto, sans-serif',
  },
  typing: {
    fontSize: '70px',
    fontWeight: '500',
    fontFamily: 'Roboto, sans-serif',
    background: 'linear-gradient(90deg, #ff5f6d, #ffc371)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    borderRight: '2px solid #000', // Cursor effect
    animation: 'blink 0.75s step-end infinite', // Only the blinking cursor needs animation here
  },
};

// Keyframes for the blinking cursor animation
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(`
  @keyframes blink {
    from, to { border-color: transparent; }
    50% { border-color: black; }
  }
`, styleSheet.cssRules.length);

export default HomeScreen;
