import React from 'react';
import Slideshow from './Slideshow'; // Import the Slideshow component

function AboutMe() {
  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.title}>About Me.</h1>
        <p style={styles.paragraph}>
          I'm Wesley Lu, a computer science student at Georgia Tech specializing in Artificial Intelligence and System Architecture. I'm passionate about finance and technology, with a strong focus on developing algorithmic trading strategies powered by machine learning.
        </p>
        <p style={styles.paragraph}>
          Outside of coding, I appreciate nature through exercising, sightseeing, and snowboarding. I enjoy staying active, trying new restaurants, and exploring new hobbies/interests. I also enjoy poker and chess.
        </p>
      </div>
      <div style={styles.slideshowContainer}>
        <Slideshow /> {}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center', 
    alignItems: 'center', 
    maxWidth: '80vw', 
    padding: '2vw', 
    margin: '0 auto',
    color: '#333',
    fontFamily: 'Roboto, Arial, sans-serif',
    backgroundColor: '#ffffff',
    minHeight: '100vh',
  },
  textContainer: {
    flex: 1,
    marginRight: '4vw',
  },
  slideshowContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '25vw',
  },
  title: {
    fontSize: '5vw',
    fontWeight: 'bold',
    color: '#000000',
    marginBottom: '2vh', 
    paddingLeft: '5vw',
  },
  paragraph: {
    fontSize: '2vw', // Relative font size
    lineHeight: '1.6',
    marginBottom: '1.5vh', // Relative margin
    fontWeight: '400',
    paddingLeft: '5vw', // Relative padding
  },
};

export default AboutMe;
