import React, { useEffect, useState } from 'react';

function HomeScreen() {
  const [text, setText] = useState('');
  const fullText = "i am developer, designer, researcher";

  useEffect(() => {
    let index = 0; // Move index inside useEffect so it starts fresh on component mount
    const interval = setInterval(() => {
      setText(fullText.slice(0, index + 1));
      index++;
      if (index === fullText.length) {
        clearInterval(interval); // Stop interval once fullText is fully typed
      }
    }, 100); // Adjust typing speed here

    return () => clearInterval(interval); // Clean up interval on unmount
  }, []);

  return (
    <div style={styles.homeContainer}>
      <nav style={styles.navbar}>
        <ul style={styles.navLinks}>
          <li>home</li>
          <li>about</li>
          <li>experience</li>
          <li>projects</li>
          <li>skills</li>
          <li>resume</li>
        </ul>
      </nav>
      <div style={styles.content}>
        <h2 style={styles.title}>hello.</h2>
        <h2 style={styles.title}>iâ€™m wesley lu.</h2>
        <h2 style={styles.typing}>{text}</h2>
      </div>
    </div>
  );
}

const styles = {
  homeContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    padding: '20px',
    height: '100vh',
    backgroundColor: '#ffffff',
  },
  navbar: {
    width: '100%',
    padding: '20px 40px',
    display: 'flex',
    justifyContent: 'flex-start',
    backgroundColor: '#ffffff',
    position: 'fixed',
    top: 0,
    left: 0,
    zIndex: 1,
  },
  navLinks: {
    listStyleType: 'none',
    display: 'flex',
    gap: '20px',
    padding: 0,
    margin: 0,
    fontSize: '18px',
    color: '#333',
    fontWeight: 'bold',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100vh',
    paddingLeft: '40px',
    textAlign: 'left',
  },
  title: {
    fontSize: '80px', // Updated font size
    fontWeight: 'bold', // Make text bold
    color: '#000000',
    margin: '0px 0 10px 0', 
  },
  typing: {
    margin: '0px 0',
    fontSize: '80px', // Matching font size with title
    fontWeight: 'bold',
    background: 'linear-gradient(90deg, #007AFF, #00C6FF)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    borderRight: '2px solid #000', // Cursor effect
    animation: 'blink 0.75s step-end infinite', // Only blink cursor effect
  },
};

// Keyframes for the blinking cursor animation
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(`
  @keyframes blink {
    from, to { border-color: transparent; }
    50% { border-color: black; }
  }
`, styleSheet.cssRules.length);

export default HomeScreen;
